SVA:
property ReqAckHandshake;
  @(posedge clk) disable iff (!rst_n)
  req |-> ##[1:3] ack;
endproperty
assert property (ReqAckHandshake);

property DataStable;
  @(posedge clk) disable iff (!rst_n)
  valid |-> $stable(data) until ack;
endproperty
assert property (DataStable);

property FifoNotOverflow;
  @(posedge clk) disable iff (!rst_n)
  (count == MAX_DEPTH) |-> !push;
endproperty
assert property (FifoNotOverflow);

property OneHotState;
  @(posedge clk) disable iff (!rst_n)
  $onehot(state);
endproperty
assert property (OneHotState);

property RiseAckOnReq;
  @(posedge clk) disable iff (!rst_n)
  $rose(req) |=> ##[0:5] $rose(ack);
endproperty
assert property (RiseAckOnReq);

property ErrorFlageotid;
  ( status_output_buffer_12 ) == ( 2'h2 ) |=> fsm_7 == err_20 ;
endproperty
assert property (@(posedge clk) ErrorFlageotid);

property BurstLength;
  @(posedge clk) disable iff (!rst_n)
  (burst_start && burst_len == 4) |-> (valid)[*4] ##1 burst_done;
endproperty
assert property (BurstLength);

property NoBackToBackReq;
  @(posedge clk) disable iff (!rst_n)
  req |=> !req;
endproperty
assert property (NoBackToBackReq);

property GrantWithinN;
  @(posedge clk) disable iff (!rst_n)
  req |-> ##[1:10] gnt;
endproperty
assert property (GrantWithinN);

property ReadDataValid;
  @(posedge clk) disable iff (!rst_n)
  (read_en && !empty) |=> data_valid;
endproperty
assert property (ReadDataValid);
