@(posedge axi_clk)     aw_valid_i && !aw_ready_i |-> ##[1:$] aw_ready_i or !aw_valid_i;
endproperty

sva2:
property no_write_channel_deadlock;
@(posedge axi_clk)     (aw_valid_i && !aw_ready_i) |-> ##[1:$] (aw_ready_i || !aw_valid_i)
endproperty
assert_no_write_channel_deadlock:assert property (no_write_channel_deadlock) else $error("");

property lane_skew_compensation;
@(posedge serdes_clk)         $rose(lane_sync_done) |-> ##[0:10] (lane_skew < max_skew_threshold)
endproperty
assert_lane_skew_compensation:assert property (lane_skew_compensation) else $error("");

property data_valid_after_pwr_up;
@(posedge ddr_clk)     $fell(ddr_pwr_down) |-> ##[10:100] ddr_data_valid
endproperty
assert_data_valid_after_pwr_up:assert property (data_valid_after_pwr_up) else $error("");

property rx_termination_adjustment;
@(posedge serdes_clk)         $rose(impedance_mismatch) |-> ##[1:10] $rose(rx_term_adjust)
endproperty
assert_rx_termination_adjustment:assert property (rx_termination_adjustment) else $error("");

sva1:
property usb_pid_sequence_valid;
@(posedge usb_clk)     (usb_pid_valid) |-> (usb_pid_type inside {DATA0, DATA1, ACK, NAK, STALL});
endproperty

sva2:
property usb_pid_sequence_valid;
@(posedge usb_clk)     usb_pid_valid == 1'b1 |-> usb_pid_type inside {DATA0, DATA1, ACK, NAK, STALL}
endproperty
assert_usb_pid_sequence_valid:assert property (usb_pid_sequence_valid) else $error("");

sva1:
property multicast_filtering;
@(posedge eth_clk)     (eth_pkt_valid && eth_pkt_multicast && !multicast_group_enabled) |=> pkt_drop;
endproperty

sva2:
property multicast_filtering;
@(posedge eth_clk)         (eth_pkt_valid && eth_pkt_multicast && !multicast_group_enabled) |=> pkt_drop
endproperty
assert_multicast_filtering:assert property (multicast_filtering) else $error("");

property error_frame_length;
@(posedge can_clk)         $rose(can_error_flag_detected) |-> ##[6:12] $fell(can_error_flag_detected)
endproperty
assert_error_frame_length:assert property (error_frame_length) else $error("");

property spi_data_phase_alignment;
@(posedge mcu_clk)     (spi_cs_o && $changed(spi_mosi_o)) |-> ##[0:1] !$changed(spi_sck_o)
endproperty
assert_spi_data_phase_alignment:assert property (spi_data_phase_alignment) else $error("");

property metadata_integrity;
@(posedge flash_clk)         $rose(power_good) |-> $stable(metadata_area) throughout (initialization_done == 1)
endproperty
assert_metadata_integrity:assert property (metadata_integrity) else $error("");

property pwr_stable_after_on;
@(posedge clk_pwr)     ($rose(pwr_on_ack_layer1_) && $rose(pwr_on_ack_layer2_) && $rose(pwr_on_ack_layer3_))      |->     ##[10:100] ($stable(pwr_good_layer1_) && $stable(pwr_good_layer2_) && $stable(pwr_good_layer3_))
endproperty
assert_pwr_stable_after_on:assert property (pwr_stable_after_on) else $error("");

property p_io_pad_retention;
@(posedge mcu_clk)     (io_pad_sleep_en && $fell(io_pad_sleep_en)) |-> (io_pad_state == $past(io_pad_state, 2))
endproperty
assert_p_io_pad_retention:assert property (p_io_pad_retention) else $error("");

property p_mipi_power_down_recovery;
@(posedge mipi_clk)     $rose(power_good) |-> ##[1:10] controller_ready
endproperty
assert_p_mipi_power_down_recovery:assert property (p_mipi_power_down_recovery) else $error("");

property multi_plane_op_sync;
@(posedge nand_clk)     $rose(plane0_cmd_valid) |-> ##[1:4] $rose(plane1_cmd_valid)
endproperty
assert_multi_plane_op_sync:assert property (multi_plane_op_sync) else $error("");

property handshake_timeout_recovery;
@(posedge usb_clk)         (usb_handshake_timeout && $past(usb_tx_active, 2)) |=> ##[1:8] usb_handshake_retry
endproperty
assert_handshake_timeout_recovery:assert property (handshake_timeout_recovery) else $error("");

property address_cycle_count;
@(posedge nand_clk)         $rose(addr_latch_en) |-> ##[4:6] $fell(addr_latch_en)
endproperty
assert_address_cycle_count:assert property (address_cycle_count) else $error("");

property p_cable_flip_cc_change;
@(posedge pd_clk)         $rose(cable_flipped) |-> ##[1:5] (cc1_state != cc2_state)
endproperty
assert_p_cable_flip_cc_change:assert property (p_cable_flip_cc_change) else $error("");

property overcurrent_recovery;
@(posedge pd_clk)     $fell(overcurrent_detected) |=> ##[1:5] $rose(normal_current_mode)
endproperty
assert_overcurrent_recovery:assert property (overcurrent_recovery) else $error("");

property aw_stable_until_handshake;
@(posedge axi_clk)     (aw_valid_i && !aw_ready_i) |=> ($stable(aw_addr_i) && $stable(aw_id_i) && $stable(aw_len_i))
endproperty
assert_aw_stable_until_handshake:assert property (aw_stable_until_handshake) else $error("");

property mode_switch_interrupt;
@(posedge pd_clk)         $changed(alt_mode_active_) |-> ##[0:1] intr_alt_mode_change_
endproperty
assert_mode_switch_interrupt:assert property (mode_switch_interrupt) else $error("");

sva1:
property sim_hotplug_recovery;
@(posedge clk_core)     $rose(sim_inserted) |-> ##[1:10] sim_state == SIM_INIT
endproperty

sva2:
property sim_hotplug_recovery;
@(posedge clk_core)         $rose(sim_inserted) |-> ##[1:10] sim_state == SIM_INIT
endproperty
assert_sim_hotplug_recovery:assert property (sim_hotplug_recovery) else $error("");

sva1:
property p_source_to_sink_transition;
@(posedge pd_clk)     (pd_role == SOURCE_ROLE) && (rx_packet == PD_CTRL_PR_SWAP) |=>      ##[1:50] (pd_role == SINK_ROLE);
endproperty

sva2:
property p_source_to_sink_transition;
@(posedge pd_clk)     (pd_role == SOURCE_ROLE) && (rx_packet == PD_CTRL_PR_SWAP) |=> ##[1:50] (pd_role == SINK_ROLE)
endproperty
assert_p_source_to_sink_transition:assert property (p_source_to_sink_transition) else $error("");

sva1:
property p_pixel_format_consistency;
@(posedge isp_clk)     (pixel_format_in_ == pixel_format_out_) throughout (isp_busy);
endproperty

sva2:
property p_pixel_format_consistency;
@(posedge isp_clk)     isp_busy throughout (pixel_format_in_ == pixel_format_out_)
endproperty
assert_p_pixel_format_consistency:assert property (p_pixel_format_consistency) else $error("");

property spare_block_availability;
@(posedge flash_clk)         $fell(spare_block_count) |-> spare_block_count > 0
endproperty
assert_spare_block_availability:assert property (spare_block_availability) else $error("");

property axi_lowpwr_sleep_all_channels_idle;
@(posedge axi_clk)     $fell(axi_pwr_ok) |-> ##[2:5] (!axi_awvalid && !axi_wvalid && !axi_arvalid)
endproperty
assert_axi_lowpwr_sleep_all_channels_idle:assert property (axi_lowpwr_sleep_all_channels_idle) else $error("");

property sensitive_bus_unknown_after_reset;
@(posedge clk_core)         $rose(reset_n) |-> ##[1:5] !$isunknown(secure_bus_internal)
endproperty
assert_sensitive_bus_unknown_after_reset:assert property (sensitive_bus_unknown_after_reset) else $error("");

sva1:
property read_data_continuity;
@(posedge axi_clk)     (axi_rvalid && !axi_rlast) |=>      axi_rvalid[*1:16] ##0 axi_rlast;
endproperty

sva2:
property read_data_continuity;
@(posedge axi_clk)         (axi_rvalid && !axi_rlast) |=> (axi_rvalid[*1:16] ##0 axi_rlast)
endproperty
assert_read_data_continuity:assert property (read_data_continuity) else $error("");
