reference_sva:
property p_O_assignment_logic;
    @(posedge clk) disable iff (rst == 1)
        (B == 1 && D == 1 && A == 0 && C == 0) |-> (O == ((B & D) | (~A & ~C)));
endproperty
assert_p_O_assignment_logic: assert property (p_O_assignment_logic) else $error("Assertion failed: O signal does not reflect the correct assignment logic when B == 1, D == 1, A == 0, and C == 0");

generated_sva:
property p_output_assignment_check;
  @(posedge clk) disable iff (rst)
    (B == 1'b1 && D == 1'b1 && A == 1'b0 && C == 1'b0) |-> (O == ((B & D) | (~A & ~C)));
endproperty

assert property (p_output_assignment_check);
