reference_sva:
property p_Rd_blocking_assignment;
    @(posedge Clk) disable iff (Rst == 1)
        (RE == 1 && EF == 0) |-> (Rd == (RE == 1 && EF == 0));
endproperty
assert_p_Rd_blocking_assignment: assert property (p_Rd_blocking_assignment) else $error("Assertion failed: Rd signal does not reflect the correct state when RE is 1 and EF is 0");

generated_sva:
// Property: Rd must immediately reflect the state of (RE == 1 && EF == 0)
// When RE is 1 and EF is 0, Rd must be 1 in the same cycle
// When the condition is not met, Rd must be 0

property rd_reflects_condition;
    @(posedge Clk) disable iff (Rst)
    Rd == (RE == 1 && EF == 0);
endproperty

assert property (rd_reflects_condition)
else $error("Rd does not correctly reflect the condition (RE == 1 && EF == 0)");

// Alternative formulation with explicit cases:

// When condition is true, Rd must be 1
property rd_high_when_condition_true;
    @(posedge Clk) disable iff (Rst)
    (RE == 1 && EF == 0) |-> (Rd == 1);
endproperty

assert property (rd_high_when_condition_true)
else $error("Rd is not 1 when RE == 1 and EF == 0");

// When condition is false, Rd must be 0
property rd_low_when_condition_false;
    @(posedge Clk) disable iff (Rst)
    !(RE == 1 && EF == 0) |-> (Rd == 0);
endproperty

assert property (rd_low_when_condition_false)
else $error("Rd is not 0 when condition (RE == 1 && EF == 0) is false");
