reference_sva:
property p_pre_sign_assignment;
    @(posedge i_clk) disable iff (i_rst == 1)
        i_wr == 1 |-> ##1 pre_sign == $past(i_signed);
endproperty
assert_p_pre_sign_assignment: assert property (p_pre_sign_assignment) else $error("Assertion failed: pre_sign does not match the previous value of i_signed one cycle after i_wr is asserted");

generated_sva:
property p_write_signed;
  @(posedge i_clk) disable iff (i_rst)
  i_wr |=> (pre_sign == $past(i_signed, 1));
endproperty

assert property (p_write_signed);
