reference_sva:
property p_rd_data_c_assignment;
    @(posedge rd_clk) disable iff (rd_reset_n == 0)
        (rd_reset_n == 1 && rd_ptr >= 0 && rd_ptr < (1 << AW)) |-> rd_data_c == mem[rd_ptr[AW-1:0]];
endproperty
assert_p_rd_data_c_assignment: assert property (p_rd_data_c_assignment) else $error("Assertion failed: rd_data_c does not match the expected memory value at rd_ptr");

