reference_sva:
property p_dstb_assignment;
    @(posedge i_clk)
        (r_dstb_n == 0 && l_dstb_n == 1) |-> (dstb == (~r_dstb_n & l_dstb_n));
endproperty
assert_p_dstb_assignment: assert property (p_dstb_assignment) else $error("Assertion failed: dstb signal does not reflect the correct state when r_dstb_n is 0 and l_dstb_n is 1");

generated_sva:
property p_dstb_logic_check;
  @(posedge i_clk)
  (r_dstb_n == 1'b0 && l_dstb_n == 1'b1) |-> (dstb == (~r_dstb_n & l_dstb_n));
endproperty

assert property (p_dstb_logic_check)
else $error("dstb does not correctly reflect (~r_dstb_n & l_dstb_n) when r_dstb_n=0 and l_dstb_n=1");
