reference_sva:
property p_minx_assignment_logic;
    @(posedge clk) disable iff (rst)
        (DMG == 1 && dp[data_bit-1:data_bit-x_bit] < minx1) |-> minx == dp[data_bit-1:data_bit-x_bit];
endproperty
assert_p_minx_assignment_logic: assert property (p_minx_assignment_logic) else $error("Assertion failed: minx does not match the expected value from dp[data_bit-1:data_bit-x_bit] when DMG is 1 and dp[data_bit-1:data_bit-x_bit] is less than minx1");
