reference_sva:
property p_TxUsedDataIn_q_reset;
    @(posedge MTxClk) disable iff (TxReset == 0)
        TxReset == 1 |-> ##1 TxUsedDataIn_q == 0;
endproperty
assert_p_TxUsedDataIn_q_reset: assert property (p_TxUsedDataIn_q_reset) else $error("Assertion failed: TxUsedDataIn_q is not 0 one cycle after TxReset is asserted");

generated_sva:
property p_tx_used_data_in_clear_after_reset;
  @(posedge MTxClk) disable iff (!TxReset)
  TxReset |=> (TxUsedDataIn_q == 1'b0);
endproperty

a_tx_used_data_in_clear_after_reset: assert property (p_tx_used_data_in_clear_after_reset)
  else $error("TxUsedDataIn_q was not cleared to 0 one clock cycle after TxReset assertion");
