reference_sva:
property p_r_busy_reset;
    @(posedge i_clk) disable iff (i_rst == 0)
        (i_rst == 1) |-> ##1 (r_busy == 0);
endproperty
assert_p_r_busy_reset: assert property (p_r_busy_reset) else $error("Assertion failed: r_busy signal is not 0 one cycle after i_rst is asserted");

generated_sva:
property p_busy_clear_after_reset;
  @(posedge i_clk) disable iff (!i_rst)
  i_rst |=> (r_busy == 1'b0);
endproperty

assert property (p_busy_clear_after_reset)
  else $error("r_busy did not become inactive one clock cycle after reset assertion");
