reference_sva:
property p_o_rdata_assignment;
    @(posedge i_clk)
        i_rdata == 8'hA5 |-> o_rdata == 8'hA5;
endproperty
assert_p_o_rdata_assignment: assert property (p_o_rdata_assignment) else $error("Assertion failed: o_rdata does not equal 8'hA5 when i_rdata is 8'hA5 at the rising edge of i_clk");
