reference_sva:
property p_cur_state_reset;
    @(posedge clk_i) disable iff (rst_i == 0)
        rst_i == 1 |-> ##1 cur_state == $past(STOPPED);
endproperty
assert_p_cur_state_reset: assert property (p_cur_state_reset) else $error("Assertion failed: cur_state is not equal to the previous STOPPED state after reset");

generated_sva:
property p_reset_state;
  @(posedge clk_i) disable iff (!rst_i)
  rst_i |=> (cur_state == $past(STOPPED));
endproperty

a_reset_state: assert property (p_reset_state)
  else $error("cur_state did not equal the previous STOPPED state after reset");
