reference_sva:
property p_is_inside_screen;
    @(posedge clk_i) disable iff (rst_i)
        (minor_o == 1 && major_o == 0) |-> (is_inside_screen == 1);
endproperty
assert_p_is_inside_screen: assert property (p_is_inside_screen) else $error("Assertion failed: is_inside_screen signal is not 1 when minor_o is 1 and major_o is 0");
