reference_sva:
property p_r_freeze_compare_assignment;
    @(posedge clk) disable iff (run_i == 1)
        run_i == 0 |-> ##1 r_freeze_compare == 0;
endproperty
assert_p_r_freeze_compare_assignment: assert property (p_r_freeze_compare_assignment) else $error("Assertion failed: r_freeze_compare is not 0 one cycle after run_i is 0");
