property p_ReceivedPacketTooBig_TakeSample;
    @(posedge MRxClk) disable iff (Reset == 1)
        (Reset == 0 && LoadRxStatus == 0 && TakeSample == 1 && (~r_HugEn && RxByteCnt[15:0] > r_MaxFL[15:0])) |-> ##1 (ReceivedPacketTooBig == 1);
endproperty
assert_p_ReceivedPacketTooBig_TakeSample: assert property (p_ReceivedPacketTooBig_TakeSample) else $error("Assertion failed: ReceivedPacketTooBig signal should be 1 when the packet size exceeds the maximum frame length and TakeSample is active");
