reference_sva:
property p_grant_reset_logic;
    @(posedge clk_i) disable iff (rst_i == 0)
        rst_i == 1 |-> ##1 grant == {RCNT{1'b0}};
endproperty
assert_p_grant_reset_logic: assert property (p_grant_reset_logic) else $error("Assertion failed: grant signal is not reset to zero as expected after reset");
