reference_sva:
property p_module_sel;
    @(posedge wb_clk_i)
    disable iff (wb_rst_i == 1)
    (wb_cyc_i == 1 && wb_stb_i == 1) |-> module_sel == (wb_cyc_i == 1 && wb_stb_i == 1);
endproperty
assert_p_module_sel: assert property (p_module_sel) else $error("Assertion failed: module_sel does not reflect the correct state when wb_cyc_i and wb_stb_i are both asserted");

generated_sva:
property module_sel_valid_bus_cycle;
  @(posedge wb_clk_i) disable iff (wb_rst_i)
    (wb_cyc_i && wb_stb_i) |-> module_sel;
endproperty

assert property (module_sel_valid_bus_cycle)
  else $error("module_sel not asserted during valid Wishbone bus cycle");
