reference_sva:
property p_wb_wacc_assignment;
    @(posedge wb_clk_i)
    disable iff (wb_rst_i)
    (module_sel == 1 && wb_we_i == 1 && (wb_ack_o == 1 || SINGLE_CYCLE == 1)) |-> 
    wb_wacc == (module_sel == 1 && wb_we_i == 1 && (wb_ack_o == 1 || SINGLE_CYCLE == 1));
endproperty
assert_p_wb_wacc_assignment: assert property (p_wb_wacc_assignment) else $error("Assertion failed: wb_wacc signal does not reflect the correct state when module_sel, wb_we_i, and wb_ack_o or SINGLE_CYCLE conditions are met");

generated_sva:
property p_wb_wacc_check;
  @(posedge wb_clk_i) disable iff (wb_rst_i)
  wb_wacc == (module_sel && wb_we_i && (wb_ack_o || SINGLE_CYCLE));
endproperty

assert property (p_wb_wacc_check);
