reference_sva:
property p_o_data_assignment;
    @(posedge rd_clk)
        rd_pointer[1:0] == 2'b01 |-> o_data == data[rd_pointer[1:0]];
endproperty
assert_p_o_data_assignment: assert property (p_o_data_assignment) else $error("Assertion failed: o_data does not match data at the specified read pointer value");
