reference_sva:
property p_product_assignment_logic;
    @(posedge clk) disable iff (rst == 1)
        (rst == 0 && enable == 1) |-> ##1 product == { $past(sum_8), $past(sum_7[16:0]), $past(sum_5[6:0]), $past(sum_4[9:0]), $past(sum_2[6:0]), $past(sum_1[9:0]), $past(sum_0[6:0]), $past(product_a[16:0]) };
endproperty
assert_p_product_assignment_logic: assert property (p_product_assignment_logic) else $error("Assertion failed: product signal does not match the expected concatenation of past values.");
