reference_sva:
property p_valid_o_reset;
    @(posedge clk_i) disable iff (rst_i==0)
        (rst_i == 1) |-> ##1 (valid_o == 0);
endproperty
assert_p_valid_o_reset: assert property (p_valid_o_reset) else $error("Assertion failed: valid_o signal is not 0 one cycle after rst_i is asserted");

generated_sva:
property p_valid_o_deassert_after_reset;
    @(posedge clk_i) disable iff (!rst_i)
    $rose(rst_i) |=> !valid_o;
endproperty

assert property (p_valid_o_deassert_after_reset);
