reference_sva:
property p_sfr_mx_assignment;
    @(posedge gclk) disable iff (grst == 1)
        (grst == 0 && dena == 1 && sfr_sel[2:0] == 3'o5) |-> ##1 (sfr_mx == {30'd0, $past(rESR_C)});
endproperty
assert_p_sfr_mx_assignment: assert property (p_sfr_mx_assignment) else $error("Assertion failed: sfr_mx does not match the expected value after the specified conditions are met.");

generated_sva:
property p_sfr_mx_esr_c_check;
  @(posedge gclk) disable iff (grst)
  (!grst && dena && (sfr_sel == 3'o5)) |=> (sfr_mx == {30'b0, $past(rESR_C)});
endproperty

a_sfr_mx_esr_c_check: assert property (p_sfr_mx_esr_c_check)
  else $error("sfr_mx does not match the expected value {30'b0, rESR_C} after sfr_sel == 3'o5 with dena active");
