reference_sva:
property p_bra_ex_assignment_on_grst;
    @(posedge gclk) disable iff (grst == 0)
        grst == 1 |-> ##1 bra_ex == 2'h0;
endproperty
assert_p_bra_ex_assignment_on_grst: assert property (p_bra_ex_assignment_on_grst) else $error("Assertion failed: bra_ex is not assigned to 2'h0 one cycle after grst is asserted");
