reference_sva:
property p_service_cop_assignment;
    @(posedge bus_clk) disable iff (async_rst_b == 1)
        async_rst_b == 0 |-> ##1 service_cop == 0;
endproperty
assert_p_service_cop_assignment: assert property (p_service_cop_assignment) else $error("Assertion failed: service_cop is not 0 one cycle after async_rst_b is deasserted");

generated_sva:
property p_service_cop_after_reset_deassert;
  @(posedge bus_clk) disable iff (async_rst_b == 1'b1)
  $fell(async_rst_b) |=> (service_cop == 1'b0);
endproperty

assert property (p_service_cop_after_reset_deassert)
  else $error("service_cop not set to 0 one bus_clk cycle after async_rst_b deasserted");
