property p_wdat_captures_iowd;
  @(posedge ckmb) disable iff (reset)
  (IO_Addr_Strobe && IO_Write_Strobe && (IO_Address[4:2] == 3'b000)) |=> (wdat[31:0] == $past(iowd));
endproperty

assert property (p_wdat_captures_iowd);
