reference_sva:
property p_ReceivedPacketTooBig_Reset;
    @(posedge MRxClk) disable iff (Reset == 0)
        Reset == 1 |-> ##1 ReceivedPacketTooBig == 0;
endproperty
assert_p_ReceivedPacketTooBig_Reset: assert property (p_ReceivedPacketTooBig_Reset) else $error("Assertion failed: ReceivedPacketTooBig signal should be 0 one cycle after Reset is asserted");

generated_sva:
property p_received_packet_too_big_cleared_after_reset;
  @(posedge MRxClk) disable iff (!Reset)
  $rose(Reset) |=> (ReceivedPacketTooBig == 1'b0);
endproperty

assert property (p_received_packet_too_big_cleared_after_reset)
  else $error("ReceivedPacketTooBig was not cleared after reset");
