reference_sva:
property p_wPCINC_rADR_increment;
    @(posedge gclk) disable iff (grst == 1)
        (rADR < 1) |-> (wPCINC == (rADR + 1));
endproperty
assert_p_wPCINC_rADR_increment: assert property (p_wPCINC_rADR_increment) else $error("Assertion failed: wPCINC is not equal to rADR + 1 when rADR is less than 1");
