reference_sva:
property p_sm_empty_read_mode;
    @(posedge clock) disable iff (aclr == 1)
        (aclr == 0 && $time > 0 && lpm_mode == "READ" && sm_empty == 2'b00 && usedw_in != 0) |-> ##1 (sm_empty == 2'b01);
endproperty
assert_p_sm_empty_read_mode: assert property (p_sm_empty_read_mode) else $error("Assertion failed: sm_empty should transition to 2'b01 one cycle after the specified conditions are met in READ mode.");

