reference_sva:
property p_mux_opb_assignment_logic;
    @(posedge gclk) disable iff (grst == 1)
        (wOPC[3] == 1 && opb_fwd == 0) |-> (mux_opb == {wOPC[3], opb_fwd});
endproperty
assert_p_mux_opb_assignment_logic: assert property (p_mux_opb_assignment_logic) else $error("Assertion failed: mux_opb does not reflect the correct assignment logic when wOPC[3] is 1 and opb_fwd is 0");
