reference_sva:
property p_TTMP_assignment_logic;
    @(posedge clk) disable iff (rst == 1)
        (rst == 0 && io_we == 1 && io_re == 0 && io_a == 2'b01) |-> ##1 TTMP == $past(io_di);
endproperty
assert_p_TTMP_assignment_logic: assert property (p_TTMP_assignment_logic) else $error("Assertion failed: TTMP signal does not match the previous io_di value under the specified conditions");

generated_sva:
property ttmp_write_assignment;
  @(posedge clk) disable iff (rst)
  (!rst && io_we && !io_re && (io_a == 2'b01)) |=> (TTMP == $past(io_di));
endproperty

assert property (ttmp_write_assignment);
