reference_sva:
property p_in_wreq_assignment;
    @(posedge i_clk)
        (i_req == 1 && i_write == 1) |-> (in_wreq == 1);
endproperty
assert_p_in_wreq_assignment: assert property (p_in_wreq_assignment) else $error("Assertion failed: in_wreq signal should be 1 when i_req and i_write are both 1 at the rising edge of i_clk");

generated_sva:
property p_in_wreq_assertion;
  @(posedge i_clk) disable iff (i_rst)
  (i_req && i_write) |-> in_wreq;
endproperty

assert property (p_in_wreq_assertion)
  else $error("Assertion failed: in_wreq should be 1 when both i_req and i_write are 1");
