reference_sva:
property p_MdoEn_reset;
    @(posedge Clk) disable iff (Reset == 0)
        Reset == 1 |-> ##1 MdoEn == 0;
endproperty
assert_p_MdoEn_reset: assert property (p_MdoEn_reset) else $error("Assertion failed: MdoEn signal is not 0 one cycle after Reset is asserted");

generated_sva:
property p_mdoen_reset;
  @(posedge Clk) disable iff (!Reset)
  $rose(Reset) |=> (MdoEn == 1'b0);
endproperty

assert property (p_mdoen_reset)
  else $error("MdoEn did not become 0 one clock cycle after Reset assertion");
