reference_sva:
property p_wr_ptr_reset;
    @(posedge wr_clk) disable iff (wr_reset_n == 1)
        wr_reset_n == 0 |-> ##1 wr_ptr == 0;
endproperty
assert_p_wr_ptr_reset: assert property (p_wr_ptr_reset) else $error("Assertion failed: wr_ptr is not reset to 0 one cycle after wr_reset_n is deasserted");
