reference_sva:
property p_o_full_fifo_full;
    @(posedge wr_clk)
        {~wr_pointer[2], wr_pointer[1:0]} == rd_pointer_wr |-> o_full == 1;
endproperty
assert_p_o_full_fifo_full: assert property (p_o_full_fifo_full) else $error("Assertion failed: o_full signal does not indicate FIFO full when write pointer and read pointer match the specified condition");
