reference_sva:
property p_w_n_o_quotient_MSB;
    @(posedge i_clk) disable iff (i_rst)
        o_quotient[BW-1] == 1 |-> w_n == o_quotient[BW-1];
endproperty
assert_p_w_n_o_quotient_MSB: assert property (p_w_n_o_quotient_MSB) else $error("Assertion failed: w_n does not match the MSB of o_quotient");
