reference_sva:
property p_ReceiveEnd_Reset;
    @(posedge MRxClk) disable iff (Reset == 0)
        Reset == 1 |-> ##1 ReceiveEnd == 0;
endproperty
assert_p_ReceiveEnd_Reset: assert property (p_ReceiveEnd_Reset) else $error("Assertion failed: ReceiveEnd signal is not 0 one cycle after Reset is asserted.");

generated_sva:
property p_receive_end_deassert_after_reset;
  @(posedge MRxClk) disable iff (!Reset)
  $rose(Reset) |=> (ReceiveEnd == 1'b0);
endproperty

assert property (p_receive_end_deassert_after_reset)
  else $error("ReceiveEnd was not deasserted one clock cycle after Reset became active");
