reference_sva:
property p_xwb_adr_o_assignment_on_grst;
    @(posedge gclk) disable iff (grst == 0)
        grst == 1 |-> ##1 xwb_adr_o == {((1+(AEMB_XWB-1)-(2))){1'b0}};
endproperty
assert_p_xwb_adr_o_assignment_on_grst: assert property (p_xwb_adr_o_assignment_on_grst) else $error("Assertion failed: xwb_adr_o does not match the expected assignment when grst is asserted");

generated_sva:
property p_xwb_adr_o_reset;
  @(posedge gclk) disable iff (!grst)
  grst |=> (xwb_adr_o == {(1 + (AEMB_XWB - 1) - 2){1'b0}});
endproperty

assert property (p_xwb_adr_o_reset);
