reference_sva:
property p_cop_counter_assignment;
    @(posedge cop_clk) disable iff (async_rst_b == 1)
        async_rst_b == 0 |-> ##1 cop_counter == {COUNT_SIZE{1'b1}};
endproperty
assert_p_cop_counter_assignment: assert property (p_cop_counter_assignment) else $error("Assertion failed: cop_counter is not assigned to all 1's one cycle after async_rst_b is deasserted");

generated_sva:
property p_counter_ones;
  @(posedge cop_clk) disable iff (async_rst_b)
    !async_rst_b |=> (cop_counter == {COUNT_SIZE{1'b1}});
endproperty

assert property (p_counter_ones);
