reference_sva:
property p_in_wreq_assignment;
    @(posedge i_clk)
        (i_req == 1 && i_write == 1) |-> in_wreq == (i_req == 1 && i_write == 1);
endproperty
assert_p_in_wreq_assignment: assert property (p_in_wreq_assignment) else $error("Assertion failed: in_wreq signal does not reflect the correct state when i_req and i_write are both asserted");

generated_sva:
// Property to verify in_wreq correctly reflects the AND of i_req and i_write
property p_in_wreq_reflects_write_request;
    @(posedge i_clk)
    (i_req && i_write) |-> (in_wreq == 1'b1);
endproperty

// Assertion for the property
assert property (p_in_wreq_reflects_write_request)
else $error("Assertion failed: in_wreq should be 1 when both i_req and i_write are asserted");

// Additional property to verify in_wreq is the logical AND of i_req and i_write
property p_in_wreq_equals_and;
    @(posedge i_clk)
    in_wreq == (i_req && i_write);
endproperty

assert property (p_in_wreq_equals_and)
else $error("Assertion failed: in_wreq should equal (i_req && i_write)");
