reference_sva:
property p_r_rdata_assignment;
    @(posedge clk_core) disable iff (rst_x == 1)
        rst_x == 0 |-> ##1 r_rdata == 0;
endproperty
assert_p_r_rdata_assignment: assert property (p_r_rdata_assignment) else $error("Assertion failed: r_rdata is not 0 one cycle after rst_x is deasserted");

generated_sva:
property p_rdata_zero_after_reset_deassert;
  @(posedge clk_core) disable iff (rst_x)
  $fell(rst_x) |=> (r_rdata == 0);
endproperty

assert property (p_rdata_zero_after_reset_deassert);
