reference_sva:
property p_cop_capture_assignment;
    @(posedge bus_clk) disable iff (async_rst_b == 1)
        async_rst_b == 0 |-> ##1 cop_capture == {COUNT_SIZE{1'b1}};
endproperty
assert_p_cop_capture_assignment: assert property (p_cop_capture_assignment) else $error("Assertion failed: cop_capture does not match the expected value {COUNT_SIZE{1'b1}} one cycle after async_rst_b is deasserted");

generated_sva:
property cop_capture_after_reset;
  @(posedge bus_clk) disable iff (async_rst_b)
  !$past(async_rst_b) |-> (cop_capture == {COUNT_SIZE{1'b1}});
endproperty

assert property (cop_capture_after_reset);
