reference_sva:
property p_exponent_a_assignment;
    @(posedge clk) disable iff (rst == 1)
        (rst == 0 && enable == 1) |-> ##1 exponent_a == $past(opa[62:52]);
endproperty
assert_p_exponent_a_assignment: assert property (p_exponent_a_assignment) else $error("Assertion failed: exponent_a does not match the expected value from opa[62:52] in the previous cycle");
