property p_r_busy_reset;
    @(posedge i_clk) disable iff (i_rst == 0)
        (i_rst == 1) |-> ##1 (r_busy == 0);
endproperty
assert_p_r_busy_reset: assert property (p_r_busy_reset) else $error("Assertion failed: r_busy signal should be 0 one cycle after i_rst is asserted");

