reference_sva:
property p_d_assignment_logic;
    @(posedge clk) disable iff (rst)
        (rst == 0 && datavalid == 1 && CLR == 1) |-> ##1 d == { {x_bit{1'b1}}, {x_bit{1'b0}}, {y_bit{1'b1}}, {y_bit{1'b0}} };
endproperty
assert_p_d_assignment_logic: assert property (p_d_assignment_logic) else $error("Assertion failed: 'd' signal does not match the expected pattern after the specified conditions are met");
