reference_sva:
property p_wSFT_assignment_logic;
    @(posedge gclk) disable iff (grst == 1)
    wOPC == 6'o44 |-> wSFT == 1;
endproperty
assert_p_wSFT_assignment_logic: assert property (p_wSFT_assignment_logic) else $error("Assertion failed: wSFT signal is not assigned correctly when wOPC is 6'o44");

generated_sva:
property p_wSFT_assignment;
  @(posedge gclk) disable iff (grst)
  (wOPC == 6'o44) |-> (wSFT == 1'b1);
endproperty

assert property (p_wSFT_assignment)
else $error("wSFT should be 1 when wOPC equals 6'o44");
