reference_sva:
property p_srd_read;
    @(posedge ckmb) disable iff (reset == 1)
        read == 1 |-> srd == (read == 1);
endproperty
assert_p_srd_read: assert property (p_srd_read) else $error("Assertion failed: srd signal does not reflect the correct state when read is asserted");

generated_sva:
property p_srd_reflects_read;
  @(posedge ckmb) disable iff (reset)
  read |-> srd;
endproperty

a_srd_reflects_read: assert property (p_srd_reflects_read);
