reference_sva:
property p_in_norm_out_denorm_assignment;
    @(posedge clk) disable iff (rst == 1)
        (exponent_large > 0 && exponent == 0) |-> in_norm_out_denorm == 1;
endproperty
assert_p_in_norm_out_denorm_assignment: assert property (p_in_norm_out_denorm_assignment) else $error("Assertion failed: in_norm_out_denorm signal is not assigned correctly when exponent_large > 0 and exponent == 0");
