reference_sva:
property p_step_assignment_during_reset;
    @(posedge clk) disable iff (reset == 0)
        reset == 1 |-> ##1 Step == $past(Step);
endproperty
assert_p_step_assignment_during_reset: assert property (p_step_assignment_during_reset) else $error("Assertion failed: Step signal does not match the previous value of Step1 during reset");
