reference_sva:
property p_level_0_assignment;
    @(posedge clk) disable iff (rst_n == 1)
        rst_n == 0 |-> ##1 level_0 == 0;
endproperty
assert_p_level_0_assignment: assert property (p_level_0_assignment) else $error("Assertion failed: level_0 signal is not 0 one cycle after rst_n is deasserted");

generated_sva:
property p_level_0_after_reset;
    @(posedge clk) disable iff (rst_n)
    $fell(rst_n) |=> (level_0 == 0);
endproperty

assert property (p_level_0_after_reset);
