reference_sva:
property p_debug_ena_assignment;
    @(posedge bus_clk) disable iff (async_rst_b == 1)
        async_rst_b == 0 |-> ##1 debug_ena == 1'b0;
endproperty
assert_p_debug_ena_assignment: assert property (p_debug_ena_assignment) else $error("Assertion failed: debug_ena signal is not 0 one cycle after async_rst_b is deasserted");
